  mapUrl: string | null | undefined,
  mappings: SourceMapSegment[][],
  sources: string[],
  sourcesContent: (string | null)[],
  names: string[],
  lineOffset: number,
  columnOffset: number,
  stopLine: number,
  stopColumn: number,
