import React, { useState } from "react";
import { ReactComponent as LogoIcon } from "./schwarz.svg";
import { ReactComponent as GoogleIcon } from "./google.svg";
import { ReactComponent as TwitterIcon } from "./twitter.svg";
import { ReactComponent as AppleIcon } from "./apple.svg";

const socialButtons = [
  // Your social buttons array here
];

const Popup = ({ isVisible, onClose }) => {
  // Your pop-up JSX here
};

const LoginForm = () => {
  const [popupVisible, setPopupVisible] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [emailError, setEmailError] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [showErrors, setShowErrors] = useState(false);
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [firstNameError, setFirstNameError] = useState("");
  const [lastNameError, setLastNameError] = useState("");

  const handleEmailChange = (event) => {
    const newEmail = event.target.value;
    setEmail(newEmail);

    if (!newEmail.includes("@")) {
      setEmailError('Email should contain "@" symbol');
    } else {
      setEmailError("");
    }
  };

  const handlePasswordChange = (event) => {
    const newPassword = event.target.value;
    setPassword(newPassword);

    if (newPassword.length < 8 || (newPassword.match(/\d/g) || []).length < 2) {
      setPasswordError(
        "Password should be at least 8 characters long and contain at least 2 numbers"
      );
    } else {
      setPasswordError("");
    }
  };

  const handleNextStepClick = () => {
    setEmailError("");
    setPasswordError("");
    setFirstNameError("");
    setLastNameError("");
    setShowErrors(true);

    if (!email.includes("@")) {
      setEmailError('Email should contain "@" symbol');
    }

    if (password.length < 8 || (password.match(/\d/g) || []).length < 2) {
      setPasswordError(
        "Password should be at least 8 characters long and contain at least 2 numbers"
      );
    }

    if (firstName.trim() === "") {
      setFirstNameError("Please enter your first name");
    } else if (lastName.trim() === "") {
      setLastNameError("Please enter your last name");
    } else {
      // If there are no errors, show the pop-up
      setPopupVisible(true);
    }
  };

  return (
    <div className="container">
      {/* Your existing JSX code */}
      <a href="#" className="next" onClick={handleNextStepClick}>
        Next Step
      </a>
      {popupVisible && <Popup onClose={() => setPopupVisible(false)} />}
    </div>
  );
};

export default LoginForm;
