import React from "react";
import "./Login.css";
import { useState } from "react";
import { ReactComponent as LogoIcon } from "./schwarz.svg";
import { ReactComponent as GoogleIcon } from "./google.svg";
import { ReactComponent as TwitterIcon } from "./twitter.svg";
import { ReactComponent as AppleIcon } from "./apple.svg";

const socialButtons = [
  {
    icon: <GoogleIcon className="google-icon" />,
    text: "Continue with Google",
    className: "google-btn",
  },
  {
    icon: <AppleIcon className="apple-icon" />,
    text: "Continue with Apple",
    className: "apple-btn",
  },
  {
    icon: <TwitterIcon className="twitter-icon" />,
    text: "Continue with Twitter",
    className: "twitter-btn",
  },
];

const LoginForm = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [emailError, setEmailError] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [showErrors, setShowErrors] = useState(false);
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [firstNameError, setFirstNameError] = useState("");
  const [lastNameError, setLastNameError] = useState("");

  const handleEmailChange = (event) => {
    const newEmail = event.target.value;
    setEmail(newEmail);
    setEmailError(newEmail.includes("@") ? "" : 'Email should contain "@" symbol');
  };

  const handlePasswordChange = (event) => {
    const newPassword = event.target.value;
    setPassword(newPassword);
    const numCount = (newPassword.match(/\d/g) || []).length;
    setPasswordError(
      newPassword.length >= 8 && numCount >= 2
        ? ""
        : "Password should be at least 8 characters long and contain at least 2 numbers"
    );
  };

  const handleNextStepClick = () => {
    // Validate fields and set errors
    setFirstNameError(firstName.trim() === "" ? "Please enter your first name" : "");
    setLastNameError(lastName.trim() === "" ? "Please enter your last name" : "");
    setEmailError(email.includes("@") ? "" : 'Email should contain "@" symbol');
    const numCount = (password.match(/\d/g) || []).length;
    setPasswordError(
      password.length >= 8 && numCount >= 2
        ? ""
        : "Password should be at least 8 characters long and contain at least 2 numbers"
    );

    // Set showErrors to true to display errors when the button is clicked
    setShowErrors(true);
  };


  return (
    <div className="container">
      <div className="log-form">
        <LogoIcon className="logoIcon" />
        <p className="bold-text">Create an Account</p>
        <p className="fade-text">
          Create your Schwarz ID and unlock a world of possibilities.
        </p>

        <div className="btn-container">
          <button className="active-btn">Sign in</button>
          <button className="passive-btn">Sign up</button>
        </div>

        <div className="log-method">
          {socialButtons.map((button, index) => (
            <button key={index} className={button.className}>
              <span>{button.icon}</span> <span>{button.text}</span>
            </button>
          ))}

          {/* section of ---- or ---- */}
          <div className="or-section">
            <div className="hr-line"></div>
            <span>or</span>
            <div className="hr-line"></div>
          </div>
        </div>

        <div className="name">
        <div className="text-group">
          <label htmlFor="firstName">First Name</label>
          <input
            type="text"
            id="firstName"
            placeholder="Enter your first name"
            className={`${firstNameError && showErrors ? "error-border" : ""}`}
            value={firstName}
            onChange={(event) => {
              const newName = event.target.value;
              setFirstName(newName);
              setFirstNameError(newName.trim() === "" ? "Please enter your first name" : "");
            }}
          />
          {firstNameError && showErrors && <p className="error">{firstNameError}</p>}
        </div>

        <div className="text-group">
          <label htmlFor="firstName">First Name</label>
          <input
            type="text"
            id="firstName"
            placeholder="Enter your first name"
            className={`${firstNameError && showErrors ? "error-border" : ""}`}
            value={firstName}
            onChange={(event) => {
              const newName = event.target.value;
              setFirstName(newName);
              setFirstNameError(newName.trim() === "" ? "Please enter your first name" : "");
            }}
          />
          {firstNameError && showErrors && <p className="error">{firstNameError}</p>}
        </div>
        </div>

        <div className="log-detail">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            placeholder="Enter your email"
            className={`email-inp ${
              emailError && showErrors ? "error-border" : ""
            }`}
            value={email}
            onChange={handleEmailChange}
          />
          {emailError && showErrors && <p className="error">{emailError}</p>}

          <label htmlFor="password">Password</label>
          <input
            type="password"
            placeholder="Enter your password"
            className={`password-inp ${
              passwordError && showErrors ? "error-border" : ""
            }`}
            value={password}
            onChange={handlePasswordChange}
          />
          {passwordError && showErrors && (
            <p className="error">{passwordError}</p>
          )}
        </div>

        <a href="#" className="next" onClick={handleNextStepClick}>
          Next Step
        </a>
      </div>
      <div className="bg-vid">
        {/* Looping Video of Simulation from Blender */}
        <video autoPlay loop muted>
          <source
            src="https://media.istockphoto.com/id/1251524036/video/morphing-holographic-liquid-blobs-abstract-3d-animation.mp4?s=mp4-640x640-is&k=20&c=c8oAYJoLD-_B_xjmfGGlss3j7PRi9i5KKpmaVbyy71A=.mp4"
            type="video/mp4"
          />
        </video>
      </div>
    </div>
  );
};
export default LoginForm;
